package variable;

public class ElseIF {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

  int time = 24;
	if (time <= 24) {
	System.out.println("Good Morning");
	}
	
	else if (time < 13) {
	System.out.println("Good Afternoon");
	}
	
	else  {
		System.out.println("Good Evening");
	}
	
	}

}

